(function(){ window.Chart = function(ctx, cfg){ 
  const c = ctx.getContext ? ctx.getContext('2d') : ctx;
  const data = cfg.data.datasets[0].data;
  const labels = cfg.data.labels;
  const w = ctx.width || ctx.canvas.width, h = ctx.height || ctx.canvas.height;
  c.clearRect(0,0,w,h);
  const pad = 40; const barW = (w - pad*2) / data.length * 0.6;
  const max = Math.max.apply(null, data.concat([1]));
  data.forEach((v,i)=>{
    const x = pad + i * ((w - pad*2)/data.length) + ((w - pad*2)/data.length - barW)/2;
    const barH = (v/max)*(h - pad*2);
    c.fillStyle = '#2f80ed88'; c.fillRect(x, h-pad-barH, barW, barH);
    c.fillStyle = '#000'; c.font='12px Arial'; c.fillText(labels[i], x, h-10);
  });
  return { destroy: ()=>{} };
}; })();
